import React from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";

const projects = [
  { id: 1, name: "Casa Carreño", image: "/imagenes/1.png" },
 
];

const Home = () => {
  return (
    <div className="w-full font-[var(--fuente-cuerpo)] text-[var(--black)]">
      {/* HERO - Carrusel */}
      <div className="relative w-full h-[70vh]">
        <Carousel
          showThumbs={false}
          autoPlay
          infiniteLoop
          showStatus={false}
          interval={3000}
          transitionTime={1000}
          showArrows={false}
          stopOnHover={false}
        >
          {projects.map((project) => (
            <div key={project.id}>
              <img
                src={project.image}
                alt={project.name}
                className="w-full h-[70vh] object-cover"
              />
            </div>
          ))}
        </Carousel>
      </div>

      {/* Introducción */}
      <section className="text-center py-10 px-4">
        <h1 className="text-[28px] md:text-[36px] font-bold text-[var(--primary-900)] leading-tight">
          G.C. Integral Group
        </h1>
        <h2 className="text-[24px] md:text-[30px] font-bold text-[var(--primary-900)] leading-tight">
          Ingeniería & Arquitectura
        </h2>
        <p className="text-[16px] md:text-[18px] text-[var(--neutral-900)] mt-3 max-w-2xl mx-auto">
          Somos profesionales con más de 30 años de experiencia y reconocimiento en el sector.
        </p>
        <button className="mt-6 px-6 py-3 bg-[var(--primary-700)] text-white text-[16px] font-semibold rounded-md shadow-md hover:bg-[var(--primary-900)] transition duration-300">
          ¿Quiénes somos?
        </button>
      </section>

      {/* Grid de Proyectos */}
      <section className="grid grid-cols-1 md:grid-cols-3 gap-4 p-6 max-w-6xl mx-auto">
        {projects.map((project) => (
          <div key={project.id} className="relative">
            <img
              src={project.image}
              alt={project.name}
              className="w-full h-60 object-cover rounded-md"
            />
            <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-50 text-white text-sm p-2">
              {project.name}
            </div>
          </div>
        ))}
      </section>

      {/* Portafolio Digital */}
      <div className="text-center py-6">
        <a href="#" className="text-[var(--secondary-600)] font-semibold text-lg underline">
          Portafolio digital
        </a>
      </div>
    </div>
  );
};

export default Home;
