import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <nav className="header_container">
      {/* Logo y botón del menú en móvil */}
      <div className="header_top">
        <Link to="/" className="header_logo">
          <img src="/imagenes/logo.png" alt="Logo" className="logo" />
        </Link>
        <button className="menu_toggle" onClick={() => setMenuOpen(!menuOpen)}>
          ☰
        </button>
      </div>

      {/* Menú de navegación */}
      <div className={`options ${menuOpen ? 'open' : ''}`}>
        <ul className="list_opc_menu">
          <li className="option">
            <Link to="/Proyectos" className="menu_opc_text">Proyectos</Link>
          </li>
          <li className="option">
            <Link to="/Servicios" className="menu_opc_text">Servicios</Link>
          </li>
          <li className="option">
            <Link to="/Clientes" className="menu_opc_text">Clientes</Link>
          </li>
          <li className="option">
            <Link to="/QuienesSomos" className="menu_opc_text">Quiénes somos</Link>
          </li>
          <li className="option">
            <Link to="/Contacto" className="menu_opc_text">Contacto</Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Header;
