import React from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import "react-responsive-carousel/lib/styles/carousel.min.css"; 
import { Carousel } from 'react-responsive-carousel';

const projects = [
  { id: 1, name: "Casa Carreño", image: "/imagenes/1.png" },
  { id: 2, name: "Edificio Bethel", image: "/imagenes/2.png" },
  { id: 3, name: "Casa La Clarita", image: "/imagenes/3.png" },
  { id: 4, name: "Casa Lago Colima", image: "/imagenes/4.png" },
  { id: 5, name: "Modelo 313 Casa de Alquiler", image: "/imagenes/5.png" },
  { id: 6, name: "Camac Secado Cacao Casa Luker", image: "/imagenes/6.png" },
  { id: 7, name: "Recuperación Piscina", image: "/imagenes/7.png" },
  { id: 8, name: "Módulo Horticultural UTP", image: "/imagenes/8.png" },
  { id: 9, name: "Baño Hotel Soratama", image: "/imagenes/9.png" },
];

const Home = () => {
  return (
    <div className="w-full">
      <Header />

      {/* HERO SECTION CON CARRUSEL */}
      <div className="relative w-full h-[70vh]">
        <Carousel
          showThumbs={false}
          autoPlay={true}
          infiniteLoop={true}
          showStatus={false}
          interval={3000}
          transitionTime={1000}
          showArrows={false}
          stopOnHover={false}
        >
          {projects.map((project) => (
            <div key={project.id}>
              <img src={project.image} alt={project.name} className="w-full h-[70vh] object-cover" />
            </div>
          ))}
        </Carousel>
      </div>

      {/* INTRODUCCIÓN */}
      <section className="text-center p-6">
        <h1 className="text-3xl md:text-5xl font-bold text-blue-900">
          G.C. Integral Group <br /> Ingeniería & Arquitectura
        </h1>
        <p className="text-lg text-gray-700 mt-2">
          Somos profesionales con más de 30 años de experiencia y reconocimiento en el sector.
        </p>
        <a href="/quienes-somos" className="mt-4 inline-block bg-blue-900 text-white px-6 py-2 rounded-md hover:bg-blue-700">
          ¿Quiénes somos?
        </a>
      </section>

      {/* GALERÍA DE PROYECTOS */}
      <section className="p-6">
        <h2 className="text-center text-2xl font-bold text-blue-900 mb-4">Nuestros Proyectos</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {projects.map((project) => (
            <div key={project.id} className="relative shadow-lg rounded-lg overflow-hidden">
              <img src={project.image} alt={project.name} className="w-full h-48 object-cover" loading="lazy" />
              <div className="absolute bottom-2 left-2 bg-white/80 text-sm font-bold p-1 rounded-md">
                {project.name}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* PORTAFOLIO DIGITAL */}
      <div className="text-center my-6">
        <a href="/portafolio" className="text-lg font-bold text-green-600 hover:underline">
          Portafolio digital
        </a>
      </div>

      <Footer />
    </div>
  );
};

export default Home;
