import React from 'react';
import { motion } from 'framer-motion'; // Importamos Framer Motion
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import "react-responsive-carousel/lib/styles/carousel.min.css"; 
import { Carousel } from 'react-responsive-carousel';

const projects = [
  { id: 1, name: "Casa Carreño", image: "/imagenes/1.png" },
  { id: 2, name: "Edificio Bethel", image: "/imagenes/2.png" },
  { id: 3, name: "Casa La Clarita", image: "/imagenes/3.png" },
  { id: 4, name: "Casa Lago Colima", image: "/imagenes/4.png" },
  { id: 5, name: "Modelo 313 Casa de Alquiler", image: "/imagenes/5.png" },
  { id: 6, name: "Camac Secado Cacao Casa Luker", image: "/imagenes/6.png" },
  { id: 7, name: "Recuperación Piscina", image: "/imagenes/7.png" },
  { id: 8, name: "Módulo Horticultural UTP", image: "/imagenes/8.png" },
  { id: 9, name: "Baño Hotel Soratama", image: "/imagenes/9.png" },
];

// Texto animado
const text = "G.C. Integral Group Ingeniería & Arquitectura";

// Variantes de animación para cada letra
const splitTextVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.05, // Retraso progresivo en la animación de cada letra
    },
  }),
};

const Home = () => {
  return (
    <div className="w-full">
      <Header />

      {/* HERO SECTION CON CARRUSEL AUTOMÁTICO */}
      <div className="relative w-full h-[70vh]">
        <Carousel
          showThumbs={false}
          autoPlay={true}
          infiniteLoop={true}
          showStatus={false}
          interval={3000}
          transitionTime={1000}
          showArrows={false}
          stopOnHover={false}
        >
          {projects.map((project) => (
            <div key={project.id}>
              <img src={project.image} alt={project.name} className="w-full h-[70vh] object-cover" />
            </div>
          ))}
        </Carousel>
      </div>

      {/* SECCIÓN DE INTRODUCCIÓN CON TEXTO ANIMADO */}
      <section className="text-center py-10 px-4 md:px-20">
        <motion.h1
          className="text-2xl md:text-4xl font-bold text-blue-900 leading-tight"
          initial="hidden"
          animate="visible"
        >
          {text.split("").map((char, i) => (
            <motion.span key={i} custom={i} variants={splitTextVariants}>
              {char}
            </motion.span>
          ))}
        </motion.h1>
        <p className="text-lg text-gray-700 mt-4 max-w-2xl mx-auto">
          Somos profesionales con más de 30 años de experiencia y reconocimiento en el sector.
        </p>
        <a
          href="/quienes-somos"
          className="mt-6 inline-block bg-blue-900 text-white px-6 py-3 rounded-md hover:bg-blue-700 transition-all"
        >
          ¿Quiénes somos?
        </a>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
