import React from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // Importamos los estilos del carrusel
import { Carousel } from 'react-responsive-carousel';

const projects = [
  { id: 1, name: "Casa Carreño", image: "/imagenes/1.png" },
  //{ id: 2, name: "Edificio Bethel", image: "/imagenes/2.png" },
  //{ id: 3, name: "Casa La Clarita", image: "/imagenes/3.png" },
  //{ id: 4, name: "Casa Lago Colima", image: "/imagenes/4.png" },
 // { id: 5, name: "Modelo 313 Casa de Alquiler", image: "/imagenes/5.png" },
  //{ id: 6, name: "Camac Secado Cacao Casa Luker", image: "/imagenes/6.png" },
 // { id: 7, name: "Recuperación Piscina", image: "/imagenes/7.png" },
 // { id: 8, name: "Módulo Horticultural UTP", image: "/imagenes/8.png" },
 // { id: 9, name: "Baño Hotel Soratama", image: "/imagenes/9.png" },
];

const Home = () => {
  return (
    <div className="w-full">
      <Header />

      {/* HERO SECTION CON CARRUSEL AUTOMÁTICO */}
      <div className="relative w-full h-[70vh]">
        <Carousel
          showThumbs={false} // Oculta miniaturas
          autoPlay={true} // Se mueve automáticamente
          infiniteLoop={true} // Repetir infinitamente
          showStatus={false} // Oculta el estado de la imagen
          interval={2000} // Cambio de imagen cada 3 segundos
          transitionTime={1000} // Transición más fluida (1s)
          showArrows={false} // Ocultar flechas para una experiencia más limpia
          stopOnHover={false} // Evita que se detenga al pasar el mouse
        >
          {projects.map((project) => (
            <div key={project.id}>
              <img src={project.image} alt={project.name} className="w-full h-[70vh] object-cover" />
            </div>
          ))}
        </Carousel>
      </div>

      {/* INTRODUCCIÓN */}
      <section className="text-center p-6">
        <h1 className="text-3xl font-bold text-blue-900">G.C. Integral Group</h1>
        <p className="text-lg text-gray-700 mt-2">
          Somos profesionales con más de 30 años de experiencia en ingeniería y arquitectura.
        </p>
      </section>

      

      <Footer />
    </div>
  );
};

export default Home;
