import React from "react";
import { motion } from "framer-motion";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import "../Servicios/Servicios.css"; // Asegúrate de enlazar los estilos

// Lista de servicios con imágenes y contenido
const servicios = [
  {
    id: 1,
    title: "Impermeabilización y mantenimiento",
    description: "Damos solución a sus problemas de humedad con productos de primer...",
    image: "/imagenes/servicios/1.png",
    list: [
      "Cubiertas",
      "Muros",
      "Terrazas",
      "Ventanas",
      "Pintura",
      "Restauración estructural",
      "Recubrimientos especiales (Epóxicos industriales)",
      "Espacios deportivos",
      "Baños y pisos pulidos",
    ],
  },
  {
    id: 2,
    title: "Diseño, construcción de obras civiles y arquitectónicas",
    description: "Desarrollamos soluciones de acuerdo a las necesidades de nuestros clientes...",
    image: "/imagenes/servicios/2.png",
    list: [
      "Vivienda",
      "Industria",
      "Infraestructura",
      "Reformas y remodelaciones",
      "Edificaciones institucionales",
      "Comercial",
    ],
  },
  {
    id: 3,
    title: "Arquitectura bioclimática y paisajismo",
    description: "Diseño, construcción y mantenimiento.",
    image: "/imagenes/servicios/3.png",
    list: [
      "Cubiertas verdes",
      "Terrazas verdes",
      "Muros verdes",
    ],
  },
];

const Servicios = () => {
  return (
    <div className="services-page-container">
      <Header />

      {/* TÍTULO PRINCIPAL */}
      <section className="services-header">
        <h2 className="services-title">Catálogo de servicios</h2>
      </section>

      {/* SECCIÓN DE SERVICIOS */}
      <section className="services-container">
        {servicios.map((servicio, index) => (
          <motion.div
            key={servicio.id}
            className={`service-card ${index % 2 === 0 ? "reverse" : ""}`}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: index * 0.2 }}
          >
            {/* IMAGEN DEL SERVICIO */}
            <img src={servicio.image} alt={servicio.title} className="service-image" />

            {/* CONTENIDO DEL SERVICIO */}
            <div className="service-content">
              <h3 className="service-title">{servicio.title}</h3>
              <p className="service-description">{servicio.description}</p>
              <ul className="service-list">
                {servicio.list.map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </div>
          </motion.div>
        ))}
      </section>

      <Footer />
    </div>
  );
};

export default Servicios;
