import React, { useEffect, useState, useRef } from "react";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";
import "../styles/Home.css"; // Importa los estilos corregidos

const projects = [
  { id: 1, name: "Casa Carreño", image: "/imagenes/1.png" },
];

const Home = () => {
  const [isVisible, setIsVisible] = useState(false);
  const introRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.3 } // Se activa cuando el 30% del elemento es visible
    );

    if (introRef.current) {
      observer.observe(introRef.current);
    }

    return () => {
      if (introRef.current) {
        observer.unobserve(introRef.current);
      }
    };
  }, []);

  return (
    <div className="home-container">
      <Header />

      {/* HERO SECTION - Carrusel */}
      <div className="hero-container">
        <Carousel
          showThumbs={false}
          autoPlay={true}
          infiniteLoop={true}
          showStatus={false}
          interval={3000}
          transitionTime={1000}
          showArrows={false}
          stopOnHover={false}
        >
          {projects.map((project) => (
            <div key={project.id}>
              <img src={project.image} alt={project.name} className="hero-image" />
            </div>
          ))}
        </Carousel>
      </div>

      {/* SECCIÓN INTRODUCCIÓN CON ANIMACIÓN */}
      <section
        ref={introRef}
        className={`intro-container ${isVisible ? "fade-in-up" : ""}`}
      >
        <h1 className="intro-title">G.C. Integral Group</h1>
        <h2 className="intro-subtitle">Ingeniería & Arquitectura</h2>
        <p className="intro-text">
          Somos profesionales con más de 30 años de experiencia y reconocimiento en el sector.
        </p>

        {/* BOTÓN MEJORADO */}
        <button className="intro-button">¿Quiénes somos?</button>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
