import React from "react";
import Header from "../Header/Header";

const Home = () => {
  return (
    <div>
      <Header />
      <div className="relative w-full h-[60vh] md:h-[80vh] flex items-center justify-center bg-gray-200">
        <img
          src="/imagenes/hero.jpg"
          alt="Imagen de la empresa"
          className="w-full h-full object-cover"
        />
        <div className="absolute bg-white/80 p-6 text-center rounded-md shadow-lg">
          <h1 className="text-2xl md:text-4xl font-bold text-blue-900">
            G.C. Integral Group - Ingeniería & Arquitectura
          </h1>
          <p className="text-lg text-gray-700">¡Siempre en obra!</p>
          <a href="/contacto" className="mt-4 inline-block bg-blue-900 text-white px-6 py-2 rounded-md hover:bg-blue-700">
            Contáctanos
          </a>
        </div>
      </div>
      <section className="text-center p-6">
        <h2 className="text-3xl font-bold text-blue-900">Nuestra Experiencia</h2>
        <p className="text-lg text-gray-700 mt-2">Más de 30 años en el sector.</p>
      </section>
    </div>
  );
};

export default Home;
