import React from "react";
import { motion } from "framer-motion";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";
import "../styles/Home.css"; // Importa los estilos corregidos

const galleryProjects = [
  { id: 2, name: "Casa Carreno", image: "/imagenes/2.png", link: "#" },
  { id: 3, name: "Edificio Bethel", image: "/imagenes/3.png", link: "#" },
  { id: 4, name: "Casa La Clarita", image: "/imagenes/4.png", link: "#" },
  { id: 5, name: "Casa Lago Calima", image: "/imagenes/5.png", link: "#" },
  { id: 6, name: "Módulo 13 Casa La Alquería", image: "/imagenes/6.png", link: "#" },
  { id: 7, name: "Camas Secado Cacao Casa Luker", image: "/imagenes/7.png", link: "#" },
  { id: 8, name: "Recuperación Piso Piscina Pueblo Tapao", image: "/imagenes/8.png", link: "#" },
  { id: 9, name: "Interv Módulo Horticultural UTP", image: "/imagenes/9.png", link: "#" },
  { id: 10, name: "Baño Hotel Soratama", image: "/imagenes/10.png", link: "#" },
];

const project = [
  { id: 1, name: "Casa Carreño", image: "/imagenes/1.png" },
];

const Home = () => {
  return (
    <div className="home-container">
      <Header />

      {/* HERO SECTION - Carrusel */}
      <div className="hero-container">
        <Carousel
          showThumbs={false}
          autoPlay={true}
          infiniteLoop={true}
          showStatus={false}
          interval={3000}
          transitionTime={1000}
          showArrows={false}
          stopOnHover={false}
        >
          {project.map((project) => (
            <div key={project.id}>
              <img src={project.image} alt={project.name} className="hero-image" />
            </div>
          ))}
        </Carousel>
      </div>

      {/* INTRODUCCIÓN */}
      <section className="intro-container">
        <motion.h1
          className="intro-title"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, ease: "easeOut" }}
        >
          G.C. Integral Group
        </motion.h1>

        <motion.h1
          className="intro-title"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.2, ease: "easeOut" }}
        >
          Ingeniería & Arquitectura
        </motion.h1>

        <motion.p
          className="intro-text"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.4, ease: "easeOut" }}
        >
          Somos profesionales con más de 30 años de experiencia y reconocimiento en el sector.
        </motion.p>

        <motion.button
          className="intro-button"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.6, ease: "easeOut" }}
          whileHover={{ scale: 1.1 }}
        >
          ¿Quiénes somos?
        </motion.button>
      </section>

      <Footer />
    </div>
  );
};

export default Home;
