import React from "react";
import { motion } from "framer-motion";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import "../Servicios/Servicios.css"; // Asegúrate de enlazar los estilos

// Lista de servicios
const servicios = [
  {
    id: 1,
    title: "Impermeabilización y mantenimiento",
    description: "Damos solución a sus problemas de humedad con productos de primer...",
    images: ["/imagenes/servicio1a.png", "/imagenes/servicio1b.png"],
    list: [
      "Cubiertas",
      "Muros",
      "Terrazas",
      "Ventanas",
      "Pintura",
      "Restauración estructural",
      "Recubrimientos especiales (Epóxicos industriales)",
      "Espacios deportivos",
      "Baños y pisos pulidos",
    ],
  },
  {
    id: 2,
    title: "Diseño, construcción de obras civiles y arquitectónicas",
    description: "Desarrollamos soluciones de acuerdo a las necesidades de nuestros clientes...",
    image: "/imagenes/servicio2.png",
    list: [
      "Vivienda",
      "Industria",
      "Infraestructura",
      "Reformas y remodelaciones",
      "Edificaciones institucionales",
      "Comercial",
    ],
  },
  {
    id: 3,
    title: "Arquitectura bioclimática y paisajismo",
    description: "Diseño, construcción y mantenimiento.",
    image: "/imagenes/servicio3.png",
    list: [
      "Cubiertas verdes",
      "Terrazas verdes",
      "Muros verdes",
    ],
  },
];

const Servicios = () => {
  return (
    <div className="services-page-container">
      <Header />

      {/* TÍTULO PRINCIPAL */}
      <section className="services-header">
        <h2 className="services-title">Catálogo de servicios</h2>
      </section>

      {/* SECCIÓN SERVICIOS */}
      <section className="services-container">
        {/* PRIMER SERVICIO CON CARRUSEL */}
        <motion.div
          className="service-card first-service"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="service-carousel">
            <Carousel
              showThumbs={false}
              autoPlay={true}
              infiniteLoop={true}
              showStatus={false}
              interval={4000}
              transitionTime={1000}
              showArrows={false}
            >
              {servicios[0].images.map((image, index) => (
                <div key={index}>
                  <img src={image} alt={servicios[0].title} className="carousel-image" />
                </div>
              ))}
            </Carousel>
          </div>
          <div className="service-content">
            <h3 className="service-title">{servicios[0].title}</h3>
            <p className="service-description">{servicios[0].description}</p>
            <ul className="service-list">
              {servicios[0].list.map((item, i) => (
                <li key={i}>{item}</li>
              ))}
            </ul>
          </div>
        </motion.div>

        {/* LOS DEMÁS SERVICIOS */}
        {servicios.slice(1).map((servicio, index) => (
          <motion.div
            key={servicio.id}
            className="service-card"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: index * 0.2 }}
          >
            <img src={servicio.image} alt={servicio.title} className="service-image" />
            <div className="service-content">
              <h3 className="service-title">{servicio.title}</h3>
              <p className="service-description">{servicio.description}</p>
              <ul className="service-list">
                {servicio.list.map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </div>
          </motion.div>
        ))}
      </section>

      <Footer />
    </div>
  );
};

export default Servicios;
