import React from "react";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import "../Contacto/Contacto.css";

const Contact = () => {
  return (
    <div className="contact-container">
      <Header />

      {/* SECCIÓN DE CONTACTO */}
      <section className="contact-info">
        <div className="contact-text">
          <h2>¿Necesitas más información?</h2>
          <p>
            Solucionamos sus necesidades de mantenimiento, reparaciones,
            remodelaciones, diseños, obras civiles y arquitectónicas.
          </p>
          <img
            src="/imagenes/contacto2.png"
            alt="Siempre en obra"
            className="contact-banner"
          />
        </div>

        {/* FORMULARIO con FormSubmit */}
        <form
          className="contact-form"
          action="https://formsubmit.co/luzmarcela.dd.29@gmail.com"
          method="POST"
        >
        <input type="text" name="name" placeholder="Nombre completo" required />
  <input type="email" name="email" placeholder="Correo electrónico" required />
  <input type="tel" name="phone" placeholder="Teléfono" required />
  <textarea name="message" placeholder="Comentario" rows="4" required></textarea>

  {/* Opcional: Evitar reCAPTCHA (solo si ya activaste tu correo) */}
  <input type="hidden" name="_captcha" value="false" />

  {/* Opcional: Redirigir después del envío */}
  <input type="hidden" name="_next" value=" http://localhost:3000/" />
        </form>
      </section>

      {/* SECCIÓN EQUIPO */}
      <section className="team-section">
        <h2>Nuestro equipo está listo para atenderte</h2>
        <img
          src="/imagenes/contacto.png"
          alt="Equipo de trabajo"
          className="team-image"
        />
      </section>

      {/* PIE DE PÁGINA */}
      <Footer />
    </div>
  );
};

export default Contact;
